package com.atguigu.gmall.manage.service.impl;import com.alibaba.dubbo.config.annotation.Service;import com.atguigu.gmall.bean.PmsBaseAttrValue;import com.atguigu.gmall.manage.mapper.PmsBaseAttrValueMapper;import com.atguigu.gmall.service.PmsBaseAttrValueService;import org.springframework.beans.factory.annotation.Autowired;/** * @ClassName PmsBaseAttrValueServiceImpl * @Author 19680 * @Date 2020/1/29 10:43 * @Version 1.0 * @Description TODO **/@Servicepublic class PmsBaseAttrValueServiceImpl implements PmsBaseAttrValueService {    @Autowired    PmsBaseAttrValueMapper pmsBaseAttrValueMapper;    @Override    public PmsBaseAttrValue getBaseAttrValueById(String valueId) {        PmsBaseAttrValue pmsBaseAttrValue = new PmsBaseAttrValue();        pmsBaseAttrValue.setId(valueId);        return pmsBaseAttrValueMapper.selectOne(pmsBaseAttrValue);    }}