package com.atguigu.gmall.manage.service.impl;import com.alibaba.dubbo.config.annotation.Service;import com.atguigu.gmall.bean.PmsBaseCatalog1;import com.atguigu.gmall.bean.PmsBaseCatalog2;import com.atguigu.gmall.manage.mapper.PmsBaseCatalog2Mapper;import com.atguigu.gmall.service.PmsBaseCatalog2Servcie;import org.springframework.beans.factory.annotation.Autowired;import java.util.List;/** * @ClassName PmsBaseCatalog2ServiceImpl * @Author 19680 * @Date 2020/1/7 14:28 * @Version 1.0 * @Description TODO **/@Servicepublic class PmsBaseCatalog2ServiceImpl implements PmsBaseCatalog2Servcie {    @Autowired    PmsBaseCatalog2Mapper pmsBaseCatalog2Mapper;    @Override    public List<PmsBaseCatalog2> getCatalog2ByCatalog1Id(String catalog1Id) {        PmsBaseCatalog2 pmsBaseCatalog2 = new PmsBaseCatalog2();        pmsBaseCatalog2.setCatalog1Id(catalog1Id);        return pmsBaseCatalog2Mapper.select(pmsBaseCatalog2);    }}