package com.atguigu.gmall.manage.service.impl;import com.alibaba.dubbo.config.annotation.Service;import com.atguigu.gmall.bean.PmsBaseCatalog2;import com.atguigu.gmall.bean.PmsBaseCatalog3;import com.atguigu.gmall.manage.mapper.PmsBaseCatalog3Mapper;import com.atguigu.gmall.service.PmsBaseCatalog3Servcie;import org.springframework.beans.factory.annotation.Autowired;import java.util.List;/** * @ClassName PmsBaseCatalog3ServiceImpl * @Author 19680 * @Date 2020/1/7 14:32 * @Version 1.0 * @Description TODO **/@Servicepublic class PmsBaseCatalog3ServiceImpl implements PmsBaseCatalog3Servcie {    @Autowired    PmsBaseCatalog3Mapper pmsBaseCatalog3Mapper;    @Override    public List<PmsBaseCatalog3> getCatalog2ByCatalog1Id(String catalog2Id) {        PmsBaseCatalog3 pmsBaseCatalog3 = new PmsBaseCatalog3();        pmsBaseCatalog3.setCatalog2Id(catalog2Id);        return pmsBaseCatalog3Mapper.select(pmsBaseCatalog3);    }}