package com.atguigu.gmall.manage.controller;import com.alibaba.dubbo.config.annotation.Reference;import com.atguigu.gmall.bean.PmsBaseAttrInfo;import com.atguigu.gmall.bean.PmsBaseAttrValue;import com.atguigu.gmall.service.PmsBaseAttrInfoService;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;/** * @ClassName AttrInfoController * @Author 19680 * @Date 2020/1/7 15:49 * @Version 1.0 * @Description TODO **/@Controller@CrossOriginpublic class AttrInfoController {    @Reference    PmsBaseAttrInfoService pmsBaseAttrInfoService;    @ResponseBody    @RequestMapping("attrInfoList")    public List<PmsBaseAttrInfo> getAttrInfoList(String catalog3Id){        return pmsBaseAttrInfoService.getAtterInfoList(catalog3Id);    }    @ResponseBody    @RequestMapping("saveAttrInfo")    public String saveAttrInfo(@RequestBody PmsBaseAttrInfo pmsBaseAttrInfo){        pmsBaseAttrInfoService.savePmsBaseAttrInfo(pmsBaseAttrInfo);        return "SUCCESS";    }    @ResponseBody    @RequestMapping("getAttrValueList")    public List<PmsBaseAttrValue> getAttrValueList(String attrId){        List<PmsBaseAttrValue> list = pmsBaseAttrInfoService.getAtterValueListByAttrId(attrId);        return list;    }}