package com.atguigu.gmall.manage.controller;import com.alibaba.dubbo.config.annotation.Reference;import com.atguigu.gmall.bean.PmsBaseCatalog1;import com.atguigu.gmall.bean.PmsBaseCatalog2;import com.atguigu.gmall.bean.PmsBaseCatalog3;import com.atguigu.gmall.service.PmsBaseCatalog2Servcie;import com.atguigu.gmall.service.PmsBaseCatalog3Servcie;import com.atguigu.gmall.service.PmsBaseCatalogService;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.CrossOrigin;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;/** * @ClassName PmsBaseCatalog1Controller * @Author 19680 * @Date 2020/1/7 10:05 * @Version 1.0 * @Description TODO **/@Controller@CrossOriginpublic class CatalogController {    @Reference    PmsBaseCatalogService pmsBaseCatalogService;    @Reference    PmsBaseCatalog2Servcie pmsBaseCatalog2Servcie;    @Reference    PmsBaseCatalog3Servcie pmsBaseCatalog3Servcie;    @ResponseBody    @RequestMapping("getCatalog1")    public List<PmsBaseCatalog1> getCatalog1(){        return pmsBaseCatalogService.getCatalog1All();    }    @ResponseBody    @RequestMapping("getCatalog2")    public List<PmsBaseCatalog2> getCatalog2(String catalog1Id){        return pmsBaseCatalog2Servcie.getCatalog2ByCatalog1Id(catalog1Id);    }    @ResponseBody    @RequestMapping("getCatalog3")    public List<PmsBaseCatalog3> getCatalog3(String catalog2Id){        return pmsBaseCatalog3Servcie.getCatalog2ByCatalog1Id(catalog2Id);    }}