package com.atguigu.gmall.manage.controller;import com.alibaba.dubbo.config.annotation.Reference;import com.atguigu.gmall.bean.PmsSkuInfo;import com.atguigu.gmall.service.PmsSkuService;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;/** * @ClassName SkuController * @Author 19680 * @Date 2020/1/9 16:53 * @Version 1.0 * @Description TODO **/@CrossOrigin@Controllerpublic class SkuController {    @Reference    PmsSkuService pmsSkuService;    @ResponseBody    @RequestMapping("saveSkuInfo")    public String getProductSaleAttrListBySpuId(@RequestBody PmsSkuInfo pmsSkuInfo){        return pmsSkuService.saveSkuInfo(pmsSkuInfo);    }}