package com.atguigu.gmall.search.config;import com.google.gson.Gson;import io.searchbox.client.JestClientFactory;import io.searchbox.client.config.HttpClientConfig;/** * @ClassName SeverLink * @Author 19680 * @Date 2020/1/27 17:52 * @Version 1.0 * @Description TODO **/public class SeverLink {    public static JestClientFactory getClink(){        JestClientFactory factory = new JestClientFactory();        factory.setHttpClientConfig(new HttpClientConfig                .Builder("http://192.168.239.133:9200")                .gson(new Gson().newBuilder().setDateFormat("yyyy-MM-dd hh:mm:ss").create())                .connTimeout(200000)                .readTimeout(300000)                .multiThreaded(true)                .build()        );        return factory;    }}