package com.atguigu.gmall.config;import com.atguigu.gmall.secskill.util.RedisUtil;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * @ClassName RedisConfig * @Author 19680 * @Date 2020/1/13 11:29 * @Version 1.0 * @Description TODO **/@Configurationpublic class RedisConfig {        @Value("${spring.redis.host:192.168.239.133}")        private String host;        @Value("${spring.redis.port:6379}")        private int port;        @Value("${spring.redis.database:0}")        private int database;        @Bean        public RedisUtil getRedisUtil(){            if (host.equals("disabled")){                return null;            }            RedisUtil redisUtil = new RedisUtil();            redisUtil.initPool(host,port,database);        return redisUtil;    }}