package com.atguigu.gmall.user.service.impl;import com.alibaba.dubbo.config.annotation.Service;import com.atguigu.gmall.bean.UmsMemberReceiveAddress;import com.atguigu.gmall.bean.UmsMemberReceiveAddressExample;import com.atguigu.gmall.service.UmsMemberReceiveAddressService;import com.atguigu.gmall.user.mapper.UmsMemberReceiveAddressMapper;import org.springframework.beans.factory.annotation.Autowired;import java.util.List;/** * @ClassName UmsMemberReceiveAddressServiceImpl * @Author 19680 * @Date 2020/1/5 10:55 * @Version 1.0 * @Description TODO **/@Servicepublic class UmsMemberReceiveAddressServiceImpl implements UmsMemberReceiveAddressService {    @Autowired    private UmsMemberReceiveAddressMapper umsMemberReceiveAddressMapper;    @Override    public List<UmsMemberReceiveAddress> getUmsMemberReceiveAddressAll() {        return umsMemberReceiveAddressMapper.selectAll();    }    @Override    public UmsMemberReceiveAddress getUmsMemberReceiveAddressById(String id) {        Long umsMemberReceiveAddressId = new Long(Long.parseLong(id));        return umsMemberReceiveAddressMapper.selectByPrimaryKey(umsMemberReceiveAddressId);    }    @Override    public List<UmsMemberReceiveAddress> getUmsMeberReceiveAddressListById(String userId) {        UmsMemberReceiveAddress umsMemberReceiveAddress = new UmsMemberReceiveAddress();        umsMemberReceiveAddress.setMemberId(userId);        return umsMemberReceiveAddressMapper.select(umsMemberReceiveAddress);    }    @Override    public int saveUmsMemberReceiveAddress(UmsMemberReceiveAddress umsMemberReceiveAddress) {        return umsMemberReceiveAddressMapper.insert(umsMemberReceiveAddress);    }    @Override    public int removeUmsMemberReceiveAddressByExample(UmsMemberReceiveAddressExample example) {        return umsMemberReceiveAddressMapper.deleteByExample(example);    }    @Override    public int replaceUmsMemberReceiveAddressByPrimaryKeySelective(UmsMemberReceiveAddress umsMemberReceiveAddress) {        return umsMemberReceiveAddressMapper.updateByPrimaryKeySelective(umsMemberReceiveAddress);    }}