package com.atguigu.gmall.user.service.impl;import com.atguigu.gmall.api.bean.UmsMemberLevel;import com.atguigu.gmall.api.bean.UmsMemberLevelExample;import com.atguigu.gmall.api.mapper.UmsMemberLevelMapper;import com.atguigu.gmall.api.service.UmsMemberLevelService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @ClassName UmsMemberLevelServiceImpl * @Author 19680 * @Date 2020/1/5 10:54 * @Version 1.0 * @Description TODO **/@Servicepublic class UmsMemberLevelServiceImpl implements UmsMemberLevelService {    @Autowired    private UmsMemberLevelMapper umsMemberLevelMapper;    @Override    public List<UmsMemberLevel> getUmsMemberLevelAll() {        return umsMemberLevelMapper.selectAll();    }    @Override    public UmsMemberLevel getUmsMemberLevelById(String id) {        Long umsMemberLevelId = new Long(Long.parseLong(id));        return (UmsMemberLevel) umsMemberLevelMapper.selectByPrimaryKey(umsMemberLevelId);    }    @Override    public int saveUmsMemberLevel(UmsMemberLevel umsMemberLevel) {        return umsMemberLevelMapper.insert(umsMemberLevel);    }    @Override    public int removeMemberLevelByExample(UmsMemberLevelExample example) {        return umsMemberLevelMapper.deleteByExample(example);    }    @Override    public int replaceMemberLevelByPrimaryKeySelective(UmsMemberLevel umsMemberLevel) {        return umsMemberLevelMapper.updateByPrimaryKeySelective(umsMemberLevel);    }}