package com.atguigu.gmall.user.service.impl;import com.atguigu.gmall.user.bean.UmsMemberReceiveAddress;import com.atguigu.gmall.user.bean.UmsMemberReceiveAddressExample;import com.atguigu.gmall.user.mapper.UmsMemberReceiveAddressMapper;import com.atguigu.gmall.user.service.UmsMemberReceiveAddressService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @ClassName UmsMemberReceiveAddressServiceImpl * @Author 19680 * @Date 2020/1/5 10:55 * @Version 1.0 * @Description TODO **/@Servicepublic class UmsMemberReceiveAddressServiceImpl implements UmsMemberReceiveAddressService {    @Autowired    private UmsMemberReceiveAddressMapper umsMemberReceiveAddressMapper;    @Override    public List<UmsMemberReceiveAddress> getUmsMemberReceiveAddressByMemberId(String id) {        return umsMemberReceiveAddressMapper.selectByMemeberId(id);    }    @Override    public List<UmsMemberReceiveAddress> getUmsMemberReceiveAddressAll() {        return umsMemberReceiveAddressMapper.selectAll();    }    @Override    public UmsMemberReceiveAddress getUmsMemberReceiveAddressById(String id) {        Long umsMemberReceiveAddressId = new Long(Long.parseLong(id));        return umsMemberReceiveAddressMapper.selectByPrimaryKey(umsMemberReceiveAddressId);    }    @Override    public int saveUmsMemberReceiveAddress(UmsMemberReceiveAddress umsMemberReceiveAddress) {        return umsMemberReceiveAddressMapper.insert(umsMemberReceiveAddress);    }    @Override    public int removeUmsMemberReceiveAddressByExample(UmsMemberReceiveAddressExample example) {        return umsMemberReceiveAddressMapper.deleteByExample(example);    }    @Override    public int replaceUmsMemberReceiveAddressByPrimaryKeySelective(UmsMemberReceiveAddress umsMemberReceiveAddress) {        return umsMemberReceiveAddressMapper.updateByPrimaryKeySelective(umsMemberReceiveAddress);    }}