package com.atguigu.gmall.user.service.impl;import com.atguigu.gmall.api.bean.UmsMember;import com.atguigu.gmall.api.bean.UmsMemberExample;import com.atguigu.gmall.api.mapper.UmsMemberMapper;import com.atguigu.gmall.api.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class UserServiceImpl implements UserService {    @Autowired    private UmsMemberMapper umsMemberMapper;    @Override    public List<UmsMember> getUmsMemberAll() {        return umsMemberMapper.selectAll();    }    @Override    public UmsMember getUserById(String id) {        Long umsMemberId = new Long(Long.parseLong(id));        return  umsMemberMapper.selectByPrimaryKey(umsMemberId);    }    @Override    public int removeUserByExample(UmsMemberExample example) {        return umsMemberMapper.deleteByExample(example);    }    @Override    public int saveUser(UmsMember umsMember) {        return umsMemberMapper.insert(umsMember);    }    @Override    public int replaceUserByPrimaryKeySelective(UmsMember umsMember) {        return umsMemberMapper.updateByPrimaryKeySelective(umsMember);    }}