package com.atguigu.gmall.conf;import com.atguigu.gmall.interceptors.AuthInterceptor;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;/** * @ClassName WebMvcConfiguration * @Author 19680 * @Date 2020/2/10 20:31 * @Version 1.0 * @Description TODO **/@Configurationpublic class WebMvcConfiguration extends WebMvcConfigurerAdapter {    @Autowired    AuthInterceptor authInterceptor;    @Override    public void addInterceptors(InterceptorRegistry registry) {        registry.addInterceptor(authInterceptor).addPathPatterns("/**");    }}